package ApplicationMVC;import java.awt.event.ActionEvent;import java.awt.event.ActionListener;public class CalculatorController {    private CalculatorView calculatorView; /*View class reference*/    private CalculatorModel calculatorModel; /*Model class reference*/    public CalculatorController(){ /*Default constructor*/        calculatorView = null;        calculatorModel = null;    }    public CalculatorController(CalculatorView calculatorView){ /*Single parametrized constructor*/        this();        this.calculatorView = calculatorView;    }    /*Twice parametrized constructor*/    public CalculatorController(CalculatorView calculatorView, CalculatorModel calculatorModel){        this(calculatorView);        this.calculatorModel = calculatorModel;        this.calculatorView.addCalculateListener(new CalculateListener());    }    /*This is an inner class*/    private class CalculateListener implements ActionListener {        @Override        public void actionPerformed(ActionEvent actionEvent){            try{                /*Two arguments pass in addTwoNumber() method*/                calculatorModel.addTwoNumbers(calculatorView.getFirstNumber(), calculatorView.getSecondNumber());                calculatorView.setCalculateResult(calculatorModel.getCalculationValue()); /*Result is consist Model class*/            }            catch (NumberFormatException numberFormatException){                System.out.println(numberFormatException.toString());                calculatorView.displayErrMessage(numberFormatException.getMessage().toUpperCase());            }        }    }}