package ApplicationMVC;import javax.swing.*;import java.awt.event.ActionListener;public class CalculatorView {    private JFrame jFrame = null;    private final JTextField firstNumber = new JTextField(10);    private final JTextField secondNumber = new JTextField(10);    private final JButton calculationButton = new JButton("Calculate");    private final JTextField calculationSolution = new JTextField(10);    public CalculatorView(){ /*Default controller*/        jFrame = new JFrame("Calculator Example");        JPanel calPanel = new JPanel(); /*As like a container but JPanel remain swing class*/        jFrame.setSize(600, 300);        calPanel.add(firstNumber);        JLabel additionLabel = new JLabel("+");        calPanel.add(additionLabel);        calPanel.add(secondNumber);        calPanel.add(calculationButton);        calPanel.add(calculationSolution);        jFrame.add(calPanel);        jFrame.setVisible(true);        jFrame.setLayout(null);        jFrame.setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);    }    public final double getFirstNumber(){        return Double.parseDouble(firstNumber.getText());    }    public final double getSecondNumber(){        return Double.parseDouble(secondNumber.getText());    }    public final double getCalculateSolution(){        return Double.parseDouble(calculationSolution.getText());    }    public void setCalculateResult(double result){        calculationSolution.setText(Double.toString(result));    }    public void addCalculateListener(ActionListener actionListener) {        calculationButton.addActionListener(actionListener);    }    public void displayErrMessage(String errMessage){        JOptionPane.showMessageDialog(jFrame, errMessage); /*On this window*/    }}